// initTabAnchor()
function initTabAnchor() {
	var animSpeed = 650;
	var win  = jQuery(window);
	var page = jQuery('html,body');
	var header = jQuery('#header');
	var isWP8 = /MSIE 10.*Touch/.test(navigator.userAgent);
	var hash = window.location.hash;
	jQuery('.tabset').each(function(){
		var holder = jQuery(this);
		var links = holder.find('a');
		win.on('scrollTo', scrollToSection);
		// links.each(function(){
		// 	var link = jQuery(this);
		// 	if(link.attr('href').substr(1) ==  'tab-' + hash.substr(1)){
		// 		win.trigger('scrollTo');
		// 	}
		// });

		links.on('click',function(e){
			e.preventDefault();
			document.location.hash = jQuery(this).attr('href').substr(5);
		})
	});
	jQuery('.trig').each(function(){
		var link = jQuery(this);
		function clickHandler(){
			if (jQuery(this).hasClass('trig-terms')) {
				jQuery('[href="#tab-terms"]').trigger('click');
			}
			if (jQuery(this).hasClass('trig-privacy')) {
				jQuery('[href="#tab-privacy"]').trigger('click');
			}
			// scrollToSection();
		}
		link.on('click',clickHandler);
	})

	function scrollToSection()  {
		var headerHeight = header.outerHeight();
		var calcOffset = jQuery('.tab-content').offset().top - headerHeight - 35;
		if (isWP8) {
			win.scrollTop(calcOffset);
		} else {
			page.animate({
				scrollTop: calcOffset
			}, animSpeed);
		}
	}
}

