// initTabChange();
function initTabChange() {
	var animSpeed = 650;
	var win  = jQuery(window);
	var page = jQuery('html,body');
	var isWP8 = /MSIE 10.*Touch/.test(navigator.userAgent);
	var links = jQuery('.footer-links a');
	function clickHandler() {
		var linkHref = jQuery(this).attr('href');
		setTimeout(function(){
			if (window.location.hash === '#' + linkHref.split('#').pop()) {
				jQuery('[href="#tab-'+ linkHref.split('#').pop() + '"]').trigger('click');
				var calcOffset = 0;
				if (isWP8) {
					win.scrollTop(calcOffset);
				} else {
					page.animate({
						scrollTop: calcOffset
					}, animSpeed);
				}
			};
		},100)
	}
	function onLoadPage(){
		if (window.location.hash.length > 1) {
			jQuery('[href="#tab-'+ window.location.hash.substring(1, window.location.hash.length) + '"]').trigger('click');
		};
	}
	onLoadPage();
	links.on('click',clickHandler)
}

